import {
  Flex, Box, Heading, FormControl, FormLabel,
  Input, Button, FormErrorMessage
} from '@chakra-ui/react';
import { useForm } from "react-hook-form";

export default function Login() {
  const {
    handleSubmit,
    register,
    formState: { errors, isSubmitting }
  } = useForm();

  function onSubmit(values) {
    return new Promise((resolve) => {
      setTimeout(() => {
        alert(JSON.stringify(values, null, 2));
        resolve();
      }, 3000);
    });
  }

  return (
    <Flex height="100vh" alignItems="center" justifyContent="center">
      <Flex direction="column" background="gray" p={12} rounded={6}>
        <form onSubmit={handleSubmit(onSubmit)}>

          <Heading mb={6}>Iniciar Sesión</Heading>

          <div>
            <FormLabel>Correo electrónico</FormLabel>
            <Input type="email" placeholder="Ingrese correo" variant="filled" mb={3} />
          </div>

          <div>
            <FormLabel>Contraseña</FormLabel>
            <Input type="password" placeholder="Ingrese contraseña" variant="filled" mb={3} />
          </div>

          <div>
            <div>
              <Input type="checkbox" id="customCheck1" />
              <FormLabel htmlFor="customCheck1">Recuérdame</FormLabel>
            </div>
          </div>

          <Button colorScheme="blue" size="xl" mt={4} isLoading={isSubmitting}>Iniciar Sesión</Button>
          <p className="forgot-password text-right">
            ¿Olvidó su <a href="#">contraseña?</a>
          </p>
        </form>
      </Flex>
    </Flex>
  );
}
